  {name: "Exumas Cays", country_id: Country.all[4].id, theme_id: Theme.find_by(:name => "Relax/Farniente").id, description: "Snorkel in the corals and encounter some Pigs & Iguana", image: "http://exumapark.org/portals/0/Cays%20Info/Shroud%20Cay%20Cut.JPG", visitLatitude: "24.169444", visitLongitude: "-76.440278"},
  {name: "Bridgetown", country_id: Country.all[5].id, theme_id: Theme.find_by(:name => 'Culture').id, description: "Capital of the Country picturing Colonial buildings. You might see Rihanna here ;)", image: "http://barbados.org/landscap/bridgetown-og.jpg", visitLatitude: "13.0947", visitLongitude: "-59.6175"},
  {name: "Holetown", country_id: Country.all[5].id, theme_id: Theme.find_by(:name => 'Culture').id, description: "The first settlement in Barbados, Holetown, was originally named Jamestown, after its benefactor, King James I of England. The Holetown Monument (in picture) commemorates the first English landing in Barbados in 1625.", image: "http://caribbean-beat.com/wp-content/uploads/2015/04/062_Hood_133.jpg", visitLatitude: "13.186667", visitLongitude: "-59.638056"},
  {name: "Nelson Dockyard  & English Harbour", country_id: Country.all[3].id, theme_id: Theme.find_by(:name => 'Culture').id, description: "English Harbour quickly became a focal point for the establishment of a naval base in Antigua.", image: "http://photoeverywhere.co.uk/west/caribbean/nelsons_dockyard.jpg", visitLatitude: "17.0052", visitLongitude: "-61.765"},
  {name: "Oistins", country_id: Country.all[5].id, theme_id: Theme.find_by(:name => "Relax/Farniente").id, description: "Oistins operates mostly as a fishing village and a tourist hang out, and is the location of the parish church for Christ Church", image: "http://static.panoramio.com/photos/original/89490153.jpg", visitLatitude: "13.066667", visitLongitude: "-59.533333"},
  {name: "Saint James", country_id: Country.all[5].id, theme_id: Theme.find_by(:name => "Relax/Farniente").id, description: "Increasingly St. James is becoming known as the playground of the rich and famous, and a haven for sun-starved tourists.", image: "http://www.dronestagr.am/wp-content/uploads/2014/08/DSC_0055.jpg", visitLatitude: "13.216667", visitLongitude: "-59.616667"},
  {name: "Saint Peter", country_id: Country.all[5].id, theme_id: Theme.find_by(:name => 'Culture').id, description: "The parish is surrounded with white sand beaches, including those along Mullins Bay.", image: "https://1fec0e98b8.site.internapcdn.net/wp-content/uploads/2013/05/Blue_Beach_HR.jpg", visitLatitude: "13.25", visitLongitude: "-59.616667"},
  {name: "Bathsheba", country_id: Country.all[5].id, theme_id: Theme.find_by(:name => "Relax/Farniente").id, description: "Fish Town which is a Surfer Paradise !", image: "http://barbados.org/landscap/bathsheba-view1.jpg", visitLatitude: "13.21417", visitLongitude: "-59.52361"},
  {name: "Community Baboon Sanctuary", country_id: Country.all[6].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "No real baboons inhabit Belize; but Belizeans use that name for the Yucatan black howler monkey (Alouatta pigra), an endangered species that exists only in Belize, northern Guatemala and southern Mexico and is one of the largest monkeys in the Americas.", image: "http://www.ourbelizevacation.com/uploads/4/2/1/7/4217382/1108874_orig.jpg", visitLatitude: "17.504722", visitLongitude: "-88.186667"},
  {name: "Altún Ha", country_id: Country.all[6].id, theme_id: Theme.find_by(:name => 'Culture').id, description: "Maya Temple", image: "http://www.alfatravelguide.com/images_ar/belize-altun-ha.jpg", visitLatitude: "17.76395", visitLongitude: "-88.347061"},
  {name: "Caye Caulker", country_id: Country.all[6].id, theme_id: Theme.find_by(:name => "Relax/Farniente").id, description: "aye Caulker is a small limestone coral island off the coast of Belize in the Caribbean Sea", image: "http://cayecaulker.org/art/splitpicture.jpg", visitLatitude: "17.7425", visitLongitude: "-88.025"},
  {name: "San Ignacio", country_id: Country.all[6].id, theme_id: Theme.find_by(:name => 'Culture').id, description: "San Ignacio serves as the cultural-economic hub of Cayo District. It got its start from Mahogany and chicle production during British colonialism. Over time it attracted people from the surrounding areas, which led to the diverse population of the town today. San Ignacio is the largest settlement in Cayo District and the second largest in the country, after Belize City.", image: "http://www.tracycarolyn.com/wp-content/uploads/2012/12/0001(pp_w860_h573).jpg", visitLatitude: "17.160125", visitLongitude: "-89.059997"},
  {name: "Actun Tunichil Muknal", country_id: Country.all[6].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "Cave in Belize, near San Ignacio, Cayo District, notable as a Maya archaeological site that includes skeletons, ceramics, and stoneware. ", image: "http://www.cavesbranch.com/Portals/0/QuickGallery/525/1-IMG_0610.JPG", visitLatitude: "17.118", visitLongitude: "-88.866"},
  {name: "Maya Mountains", country_id: Country.all[6].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "The mountains are mainly made of Paleozoic era granite and sediments. The Maya Mountains and associated foothills contain a number of important Mayan ruins including the sites of Lubaantun,[1] Nim Li Punit, Cahal Pech,[2] and Chaa Creek.", image: "http://crocdoc.ifas.ufl.edu/projects/mayamountains/images/victoriapeak.jpg", visitLatitude: "16.494167", visitLongitude: "-89.045833"},
  {name: "Glover’s Reef", country_id: Country.all[6].id, theme_id: Theme.find_by(:name => "Relax/Farniente").id, description: "Glover's Reef is a partially submerged atoll located off the southern coast of Belize, approximately 45 kilometres from the mainland.[1] It forms part of the outermost boundary of the Belize Barrier Reef.", image: "https://upload.wikimedia.org/wikipedia/commons/thumb/6/61/Great_Blue_Hole.jpg/250px-Great_Blue_Hole.jpg", visitLatitude: "16.823", visitLongitude: "-87.791"},
  {name: "Chichicastenango", country_id: Country.all[13].id, theme_id: Theme.find_by(:name => "Immersion in native population").id, description: "Known for its traditional K'iche' Maya culture.", image: "https://upload.wikimedia.org/wikipedia/commons/f/f2/Chichicastenango_market_2009.JPG", visitLatitude: "14.933333", visitLongitude: "-91.116667"},
  {name: "Candelaria Caves", country_id: Country.all[13].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "The Candelaria Caves are a large natural cave system in the highland-lowland transition of Alta Verapaz in Guatemala between the municipalities of Chisec and Raxruha. The caves are famous for its peculiar karst phenomena and significance to the Mayan history", image: "http://paddleblogs.com/mayanwhitewater/files/2011/03/candelaria-mar-2011-61-300x225.jpg", visitLatitude: "15.483333", visitLongitude: "-90.366667"},
  {name: "La Paz", country_id: Country.all[23].id, theme_id: Theme.find_by(:name => "Immersion in native population").id, description: "The Capital city sits in a bowl surrounded by the high mountains of the altiplano. As it grew, the city of La Paz climbed the hills, resulting in varying elevations from 3,200 to 4,100 m (10,500 to 13,500 ft). Overlooking the city is towering triple-peaked Illimani, which is always snow-covered and can be seen from many parts of the city, including from the neighboring city of El Alto.", image: "https://upload.wikimedia.org/wikipedia/commons/1/10/Desfile_en_El_Alto,_Bolivia.jpg", visitLatitude: "-16.5", visitLongitude: "-68.15"},
  {name: "Isla del Sol/Lake Titicaca", country_id: Country.all[23].id, theme_id: Theme.find_by(:name => "Mix of Relax/Adventurous").id, description: "Large, deep lake in the Andes on the border of Peru and Bolivia. By volume of water, it is the largest lake in South America.", image: "http://www.explorebyyourself.com/files/lake-titicaca5.jpg", visitLatitude: "-15.75", visitLongitude: "-69.416667"},
  {name: "Sucre", country_id: Country.all[23].id, theme_id: Theme.find_by(:name => "Immersion in native population").id, description: "Constitutional capital of Bolivia, the capital of the Chuquisaca Department, and the 6th most populated city in Bolivia. Located in the south-central part of the country, Sucre lies at an elevation of 2,810 meters (9,214 feet). This relatively high altitude gives the city a cool temperate climate year-round and a much thinner air.", image: "https://calazanblog-assets.s3.amazonaws.com/media/editor-uploads/sucre_bolivia_20120810_219.jpg", visitLatitude: "-19.05", visitLongitude: "-65.25"},
  {name: "Potosi", country_id: Country.all[23].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "Potosí is a city and the capital of the department of Potosí in Bolivia. It is one of the highest cities in the world by elevation at a nominal 4,090 metres (13,420 ft) For centuries, it was the location of the Spanish colonial mint. Potosí lies at the foot of the Cerro de Potosí—sometimes referred to as the Cerro Rico (\"rich mountain\", a mountain popularly conceived of as being \"made of\" silver ore that dominates the city. The Cerro Rico is the reason for Potosí's historical importance, since it was the major supply of silver for Spain during the period of the New World Spanish Empire.", image: "http://www.voyages-bolivie.com/en/wp-content/uploads/slideshow-gallery/potosi2.jpg", visitLatitude: "-19.583333", visitLongitude: "-65.75"},
  {name: "Xunantunich", country_id: Country.all[6].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "Ancient Maya archaeological site in western Belize", image: "http://media.sdreader.com/img/croppedphotos/2011/11/16/Xunantunich.jpg", visitLatitude: "17.083889", visitLongitude: "-89.133889"},
  {name: "Orange Walk", country_id: Country.all[6].id, theme_id: Theme.find_by(:name => "Relax/Farniente").id, description: "Fourth largest town in the nation of Belize", image: "http://i222.photobucket.com/albums/dd190/mercurybz/OrangeWalkTownHall.jpg", visitLatitude: "18.075", visitLongitude: "-88.558333"},
  {name: "Lac Atitlán", country_id: Country.all[13].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "Famous Volcanic Lake", image: "http://www.aventurestropicales.com/Atitlann.jpg", visitLatitude: "14.67944", visitLongitude: "-91.20083"},
  {name: "Semuc Champey", country_id: Country.all[13].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "natural monument in the department of Alta Verapaz, Guatemala, near the Q'eqchi' Maya town of Lanquín. It consists of a natural 300 m limestone bridge, under which passes the Cahabón River. Atop the bridge is a series of stepped, turquoise pools, a popular swimming attraction.", image: "http://expertvagabond.com/wp-content/uploads/lanquin-semuc-champey-mirador-XL.jpg", visitLatitude: "15.533333", visitLongitude: "-89.961389"},
  {name: "Sud Lipez", country_id: Country.all[23].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "3 Days tour to go through the amazing Lagunas and the world famous known Salar de Uyuni", image: "http://www.paprikatours.com/wp-content/uploads/2014/08/Laguna-Colorada-Sud-Lipez.jpg", visitLatitude: "-22.066667", visitLongitude: "-67.116667"},
  {name: "Salar de Uyuni", country_id: Country.all[23].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "Biggest Salt desert in the world !", image: "http://images.boomsbeat.com/data/images/full/945/28-jpg.jpg", visitLatitude: "-20.133775", visitLongitude: "-67.489133"},
  {name: "Cochabamba", country_id: Country.all[23].id, theme_id: Theme.find_by(:name => "Immersion in native population").id, description: "Cochabamba or Quchapampa is a city in central Bolivia, located in a valley bearing the same name in the Andes mountain range", image: "https://upload.wikimedia.org/wikipedia/commons/a/a4/Cochabamba,_Cordillera_Tunari.jpg", visitLatitude: "-17.383333", visitLongitude: "-66.166667"},
  {name: "Madidi National Park", country_id: Country.all[23].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "Ranging from the glacier-covered peaks of the high Andes Mountains to the tropical rainforests of the Tuichi River, Madidi and its neighbors are recognized as one of the planet's most biologically diverse regions. National park in the upper Amazon river basin in Bolivia. Established in 1995,[2] it has an area of 18,958 square kilometres, and, along with the nearby protected (though not necessarily contiguous) areas Manuripi-Heath, Apolobamba, and (across the border in Peru) the Manu Biosphere Reserve, Madidi is part of one of the largest protected areas in the world.", image: "http://2.bp.blogspot.com/-TDJu3Mzl96E/UcX2ZsqEy0I/AAAAAAAAnK0/cw9aFN0CYz4/s1600/Madidi-National-Park-Waterfall.jpg", visitLatitude: "-13.8", visitLongitude: "-67.633333"},
  {name: "Paracas National Reserve", country_id: Country.all[30].id, theme_id: Theme.find_by(:name => "Mix of Relax/Adventurous").id, description: "The Paracas National Reserve is located in Ica, Peru and consists of the Paracas Peninsula, coastal areas and tropical desert extending to the south slightly past Punta Caimán. t includes Bahía de la Independencia (Independence Bay) and miles of coastal waters. Its main purpose is to preserve the marine ecosystem and protect the historical cultural heritage related to ancient indigenous peoples, mostly of the Paracas culture.", image: "http://jatperu.com/media/k2/galleries/148/paracas2.jpg", visitLatitude: "-14.25", visitLongitude: "-75.466667"},
  {name: "Nazca Lines", country_id: Country.all[30].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "The Nazca Lines are a series of ancient geoglyphs located in the Nazca Desert[citation needed] in southern Peru. They were designated as a UNESCO World Heritage Site in 1994. The high, arid plateau stretches more than 80 km (50 mi) between the towns of Nazca and Palpa on the Pampas de Jumana about 400 km south of Lima. Although some local geoglyphs resemble Paracas motifs, scholars believe the Nazca Lines were created by the Nazca culture between 500 B.C. and 500 A.D.", image: "http://www.ancientsummit.com/wp-content/uploads/2015/12/nazca-lines-custom-travel-to-peru-ancient-summit.jpg", visitLatitude: "-14.716667", visitLongitude: "-75.133333"},
  {name: "Inka Trail", country_id: Country.all[30].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "The Inca Trail to Machu Picchu (also known as Camino Inca or Camino Inka) consists of three overlapping trails: Mollepata, Classic, and One Day. Mollepata is the longest of the three routes with the highest mountain pass and intersects with the Classic route before crossing Warmiwañusqa (\"dead woman\"). Located in the Andes mountain range, the trail passes through several types of Andean environments including cloud forest and alpine tundra. Settlements, tunnels, and many Incan ruins are located along the trail before ending the terminus at the Sun Gate on Machu Picchu mountain. The two longer routes require an ascent to beyond 4,200 metres (13,800 ft) above sea level, which can result in altitude sickness.", image: "http://www.perutrek.biz/images/Peru_Adventure/1-Inca_trail.jpg", visitLatitude: "-13.258056", visitLongitude: "-72.263333"},
  {name: "Cañon del Pato", country_id: Country.all[30].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "The mostly rocky canyon walls are too steep and arid for cultivation, and in only a few places are the slopes of the imposingly rugged canyon suitable even for grazing domestic animals. ", image: "http://1.bp.blogspot.com/-mKh_57mLt6o/T2ETwUgj11I/AAAAAAAAATY/KhhzvNgBOcM/s1600/Huaraz+to+Canon+del+Pato+140.JPG", visitLatitude: "-8.666667", visitLongitude: "-78.05"},
  {name: "Trujillo", country_id: Country.all[30].id, theme_id: Theme.find_by(:name => "Immersion in native population").id, description: "Trujillo is a city in coastal northwestern Peru and the capital of La Libertad Region. It is the centre of the second most populous city of the country[13] and the second most populous metropolitan area of Peru. It is located on the banks of the Moche River, near its mouth at the Pacific Ocean, in the Moche Valley. This was a site of the great prehistoric Moche and Chimu cultures[14] before the Inca conquest and subsequent expansion.", image: "http://www.go2peru.com/images/trujillo_01.jpg", visitLatitude: "-8.112", visitLongitude: "-79.0288"},
  {name: "Colca Canyon", country_id: Country.all[30].id, theme_id: Theme.find_by(:name => "Immersion in native population").id, description: "It is one of the deepest in the world, second in Peru after the Cotahuasi Canyon and more than twice as deep as the Grand Canyon in the United States. The Colca Valley is a colorful Andean valley with pre-Inca roots, and towns founded in Spanish colonial times, still inhabited by people of the Collagua and the Cabana cultures. The local people maintain their ancestral traditions and continue to cultivate the pre-Inca stepped terraces.", image: "https://upload.wikimedia.org/wikipedia/commons/1/13/Colca_Canyon_Puno.jpg", visitLatitude: "-15.597222", visitLongitude: "-71.879167"},
  {name: "Parque national Torotoro", country_id: Country.all[23].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "Explore the canyons!", image: "http://www.andeantrails.co.uk/assets/uploads/14169332498b3d90599312d403bb7815264c95171f.jpg", visitLatitude: "-18.1", visitLongitude: "-65.766667"},
  {name: "Machu Picchu", country_id: Country.all[30].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "The Famous Machu Picchu !!! 15th-century Inca citadel situated on a mountain ridge 2,430 metres", image: "http://www.machupicchu.org/images/fondo-machu-picchu.jpg", visitLatitude: "-13.163333", visitLongitude: "-72.545556"},
  {name: "Lambayeque", country_id: Country.all[30].id, theme_id: Theme.find_by(:name => "Immersion in native population").id, description: "Lambayeque is a city in the Lambayeque region of northern Peru. It is notable for its exceptional museums featuring artifacts from local prehistoric archaeological sites.", image: "http://portal.andina.com.pe/EDPfotografia/Thumbnail/2015/04/05/000288294W.jpg", visitLatitude: "-6.7", visitLongitude: "-79.9"},
  {name: "Peruvian Amazon", country_id: Country.all[30].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "The Peruvian Amazon (Spanish: Amazonía del Perú) is the area of the Amazon rainforest included within the country of Peru, from east of the Andes to the borders with Ecuador, Colombia, Brazil and Bolivia. This region comprises 60% of the country and is marked by a large degree of biodiversity. Peru has the second-largest portion of the Amazon rainforest after the Brazilian Amazon.", image: "http://www.explorebyyourself.com/files/spr5d_banner_peru_amazon_river_trees.jpg", visitLatitude: "-5.708333", visitLongitude: "-78.808333"},
  {name: "Tigre", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Relax/Farniente").id, description: "The town sits on an island created by several small streams and rivers and was founded in 1820, after floods had destroyed other settlements in the area, then known as the Partido de las Conchas. The area's name derives from the “tigres” or jaguars that were hunted there, on occasions, in its early years. The area was first settled by Europeans who came to farm the land, and the port developed to serve the Delta and to bring fruit and wood from the Delta and ports upstream on the Paraná river.", image: "https://media-cdn.tripadvisor.com/media/photo-s/02/6b/4f/15/tigre.jpg", visitLatitude: "-34.416667", visitLongitude: "-58.583333"},
  {name: "St. John's Cathedral", country_id: Country.all[3].id, theme_id: Theme.find_by(:name => 'Culture').id, description: "The present cathedral with its imposing white twin towers was built on a fossilized reef, in 1845, and is now in its third incarnation, as earthquakes in 1683 and in 1745 destroyed the previous structures.", image: "http://3.bp.blogspot.com/_mjR4XxjfCvM/TMLyU0ZnV-I/AAAAAAAAALM/f8ckPAOV_vc/S748/Cathedral+01.jpg", visitLatitude: "17.1227", visitLongitude: "-61.8419"},
  {name: "Dockyard Museum", country_id: Country.all[3].id, theme_id: Theme.find_by(:name => 'Culture').id, description: "The Dockyard Museum was built in 1855 and originally served as an officers’ quarters in the Royal Navy Dockyard.", image: "http://www.paradise-islands.org/antigua-nelsons-dockyard/images/nelson's-docyard-museum.jpg", visitLatitude: "17.0116", visitLongitude: "-61.453766"},
  {name: "Flores", country_id: Country.all[13].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "Flores is a town in Petén, Guatemala. The town proper is an island on Lago Petén Itzá, connected to land by a causeway, on the other side of which lie the twin towns Santa Elena and San Benito. All three are often referred to as Flores, and are grouped in one article here accordingly.", image: "http://www.luxeinacity.com/wp-content/uploads/2014/12/Flores-Guatemala-Tikals-Island-Escape-in-Lago-de-Peten1.jpg", visitLatitude: "16.929722", visitLongitude: "-89.891666"},
  {name: "Tikal", country_id: Country.all[13].id, theme_id: Theme.find_by(:name => 'Culture').id, description: "Ruins of an ancient city found in a rainforest in Guatemala", image: "https://upload.wikimedia.org/wikipedia/commons/c/c5/Tikal-Plaza-And-North-Acropolis.jpg", visitLatitude: "17.222094", visitLongitude: "-89.623614"},
  {name: "Rurrenabaque", country_id: Country.all[23].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "In recent years it has become popular with international tourism as it is an easy gateway for visits to Madidi National Park (within the Bolivian rainforest), as well as the surrounding pampas.", image: "https://boliviatravelsite.com/Images/Attractionphotos/Rurrenabaque-02.jpg", visitLatitude: "-14.442222", visitLongitude: "-67.528333"},
  {name: "San Ignacio", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => 'Culture').id, description: "San Ignacio Miní was one of the many missions founded in 1632 by the Jesuits in what the Spanish called the Province of Paraguay in the Americas during the Spanish colonial period. It is located near present-day San Ignacio valley, some 60 km north of Posadas, Misiones Province, Argentina. In 1984 it was one of four reducciones in Argentina to be designated by UNESCO as World Heritage Sites", image: "http://www.shieldsaroundtheworld.com/origs/P0001319.jpg", visitLatitude: "-27.255278", visitLongitude: "-55.531667"},
  {name: "Quebrada de Humahuaca", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "The name quebrada (literally \"broken\") translates as a deep valley or ravine. It receives its name from Humahuaca, a small city of 11,000 inhabitants. The Grande River (Río Grande), which is dry in winter, flows copiously through the Quebrada in the summer.", image: "http://www.placestoseeinyourlifetime.com/wp-content/uploads/2014/07/Quebrada-de-Humahuaca-Photo-by-Ossian-Lindholm.jpg", visitLatitude: "-23.199722", visitLongitude: "-65.348889"},
  {name: "Arequipa", country_id: Country.all[30].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "The city was founded on August 15, 1540, by Garcí Manuel de Carbajal as \"Villa Hermosa de Nuestra Señora de la Asunción\". During the Colonial period, Arequipa became highly important for its economic prosperity and for its loyalty to the Spanish Crown. After Peru gained its independence from Spain in 1821, Arequipa acquired greater political significance,[6] and was declared the capital city of Peru from 1835 to 1883.", image: "http://images.trvl-media.com/media/content/shared/images/travelguides/Arequipa-410-smallTabletRetina.jpg", visitLatitude: "-16.383333", visitLongitude: "-71.533333"},
  {name: "Quilmes", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => 'Culture').id, description: "The Ruins of Quilmes is an archaeological site in the Calchaquí Valleys, Tucumán Province, Argentina. The site was the largest pre-Columbian settlement in the country,occupying about 30 hectares. The area dates back to ca 850 AD and was inhabited by the Quilmes people;[3] it is believed that about 5,000 people lived here during its heights.", image: "http://tours42plus.com/Assets/Uploaded-CMS-Files/e4c9aaa3-a41d-4807-95e2-ca2bff5d7c3fLas%20Ruinas%20de%20Quilmes.jpg", visitLatitude: "-26.463812", visitLongitude: "-66.03796"},
  {name: "Cachi", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "The population is found surrounded by imposing snow-covered mountains reaching as much as 5,000 meters. The temperature is usually mild and the skies are almost always clear, making the region ideal for mountain climbing. The architecture of the small city is principally of colonial Spanish style with adobe homes painted white and built over bases of rock, sporting antique window grills forged of iron.", image: "http://www.vagabondedit.com/wp-content/uploads/2012/04/Cachi_3265.jpg", visitLatitude: "-25.1194", visitLongitude: "-66.162"},
  {name: "Lago Argentino", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "The lake lies within the Los Glaciares National Park in a landscape with numerous glaciers and is fed by the glacial meltwater of several rivers, the water from Lake Viedma brought by the La Leona River, and many mountain streams. Its drainage basin amounts to more than 17,000 km2 (6,564 sq mi). Waters from Lake Argentino flow into the Atlantic Ocean through the Santa Cruz River.", image: "http://www.wallpapersxl.com/thumb/argentina-lago-argentino-hd-419800.jpg", visitLatitude: "-50.216667", visitLongitude: "-72.416667"},
  {name: "Iguazu Falls", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: " Waterfalls of the Iguazu River on the border of the Argentine province of Misiones and the Brazilian state of Paraná. The falls divide the river into the upper and lower Iguazu. The Iguazu River rises near the city of Curitiba. For most of its course, the river flows through Brazil, however, most of the falls are on the Argentine side. Below its confluence with the San Antonio River, the Iguazu River forms the boundary between Argentina and Brazil.", image: "http://visit7wonders.com/en/wp-content/uploads/sites/2/2015/10/Iguazu-Falls-6.jpg", visitLatitude: "-25.686667", visitLongitude: "-54.444722"},
  {name: "Cusco", country_id: Country.all[30].id, theme_id: Theme.find_by(:name => "Immersion in native population").id, description: "The site was the historic capital of the Inca Empire from the 13th into the 16th century until the Spanish conquest. In 1983 Cusco was declared a World Heritage Site by UNESCO.", image: "http://southamericaplanet.com/wp-content/uploads/2014/01/Cusco_Peru.jpg", visitLatitude: "-13.525", visitLongitude: "-71.972222"},
  {name: "Tilcara", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "The area features dramatic mountainous landscapes and rich aboriginal traditions, which make it a major tourist attraction in northwestern Argentina.", image: "http://www.hotelnorterupestre.com.ar/movil/assets/images/fotos/tilcara.jpg", visitLatitude: "-23.566667", visitLongitude: "-65.366667"},
  {name: "Valdes Peninsula", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "It is an important nature reserve which was listed as a World Heritage Site by UNESCO in 1999. Home to Sea lions, whales etc", image: "http://www.southamerica.travel/files/tours/itinerary/thumbs/peninsula-valdes-sea-lions.jpg", visitLatitude: "-42.766667", visitLongitude: "-65.05"},
  {name: "FitzRoy", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "Monte Fitz Roy (also known as Cerro Chaltén, Cerro Fitz Roy, or simply Mount Fitz Roy) is a mountain located near El Chaltén village, in the Southern Patagonian Ice Field in Patagonia, on the border between Argentina and Chile. First climbed in 1952 by French alpinists Lionel Terray and Guido Magnone, it remains among the most technically challenging mountains for mountaineers on Earth.", image: "http://www.mountainphotography.com/images/large/lagoTresSunrise.jpg", visitLatitude: "-49.271278", visitLongitude: "-73.043222"},
  {name: "Ushuaia", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "Ushuaia has long been described as the southernmost city in the world. Strting point to explore Las tierras del Fuego Park !", image: "http://freestyleadventuretravel.com/adventuretravel13/wp-content/uploads/2014/06/Ushuaia.jpg", visitLatitude: "-54.8", visitLongitude: "-68.3"},
  {name: "Purmamarca", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "It is located at the Quebrada de Purmamarca, sometimes considered part of the Quebrada de Humahuaca. The Cerro de los Siete Colores (Hill of the Seven Colours) shows its colourful face to the nearby town.", image: "http://www.contrastesturismo.com/resources/produccion/contenidos/medium/IMAGENES_contrastes/ARGENTINA/norte/cerro_de_los_siete_colores.jpg", visitLatitude: "-23.733333", visitLongitude: "-65.483333"},
  {name: "Perito Moreno", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "The Perito Moreno Glacier (Spanish: Glaciar Perito Moreno) is a glacier located in the Los Glaciares National Park in southwest Santa Cruz Province, Argentina.", image: "https://c2.staticflickr.com/8/7624/17050702179_ff58e6fc2c_b.jpg", visitLatitude: "-50.483333", visitLongitude: "-73.05"},
  {name: "Aconcagua", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "highest mountain outside of Asia, at 6,961 metres (22,838 ft), and by extension the highest point in both the Western Hemisphere and the Southern Hemisphere.[1] It is located in the Andes mountain range, in the Mendoza Province, Argentina, and lies 112 kilometres (70 mi) northwest of its capital, the city of Mendoza. It is one of the 7 summits.", image: "http://www.worldatlas.com/webimage/countrys/samerica/landforms/photos/aconcagua.jpg", visitLatitude: "-32.653431", visitLongitude: "-70.011083"},
  {name: "Lima", country_id: Country.all[30].id, theme_id: Theme.find_by(:name => "Immersion in native population").id, description: " Capital and the largest city of Peru. It is located in the valleys of the Chillón, Rímac and Lurín rivers, in the central coastal part of the country, overlooking the Pacific Ocean. Together with the seaport of Callao, it forms a contiguous urban area known as the Lima Metropolitan Area. With a population of almost 10 million, Lima is the most populous metropolitan area of Peru, and the second largest city in the Americas (as defined by \"city proper\"), just behind São Paulo and Mexico City.", image: "http://lamanitatravel.com/wp-content/uploads/2015/04/Lima-City2.jpg", visitLatitude: "-12.043333", visitLongitude: "-77.028333"},
  {name: "Islas Ballestas", country_id: Country.all[30].id, theme_id: Theme.find_by(:name => "Mix of Relax/Adventurous").id, description: "The Ballestas Islands are a group of small islands near the town of Paracas located within the Paracas District of the Pisco Province in the Ica Region, on the south coast of Peru. Composed largely of rock formations and covering an estimated area of 0.12 km², these islands are an important sanctuary for marine fauna like the guanay guano bird, the blue-footed booby and the tendril.", image: "http://www.turismoviajesperu.com/wordpress/wp-content/uploads/2016/01/NOC-003-03.jpg", visitLatitude: "-13.735833", visitLongitude: "-76.396389"},
  {name: "Lanquín", country_id: Country.all[13].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "A large cave complex known as Grutas de Lanquín is located at only one kilometer from the capital city of the municipality and 11 km south of Lanquín runs the Cahabon River and one can find the Semuc Champey natural monument. Lanquín is often the starting point of the tourist excursions to Semuc Champey.", image: "http://guatemalatravel.guide/wp-content/uploads/2015/08/semuc.png", visitLatitude: "15.575833", visitLongitude: "-89.981111"},
  {name: "Salta", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Immersion in native population").id, description: "Nicknamed Salta la Linda (\"Salta the beautiful\"), it has become a major tourist destination due to its old, colonial architecture, tourism friendliness, excellent weather and natural scenery of the valleys westward.", image: "http://cdni.adventure-life.com/68/79/1/qarcxudv/fullsize.jpg", visitLatitude: "-24.783333", visitLongitude: "-65.416667"},
  {name: "Bariloche", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Mix of Relax/Adventurous").id, description: "The city emerged in the 1930s and 1940s as a major tourism centre with skiing, trekking and mountaineering facilities. In addition, it has numerous restaurants, cafés, and chocolate shops. Good starting point for the Lagos ruta", image: "https://i.ytimg.com/vi/HHJS3HPiXDY/hqdefault.jpg", visitLatitude: "-41.15", visitLongitude: "-71.3"},
  {name: "Mendoza", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Mix of Relax/Adventurous").id, description: "Winery to be explored riding ! Ruta Nacional 7, the major road running between Buenos Aires and Santiago, runs through Mendoza. The city is a frequent stopover for climbers on their way to Aconcagua (the highest mountain in the Western and Southern Hemispheres) and for adventure travelers interested in mountaineering, hiking, horseback riding, rafting, and other sports. In the winter, skiers come to the city for its easy access to the Andes.", image: "http://www.tourist-destinations.com/wp-content/uploads/2011/06/20140115162013-OtonoMendoza.jpg", visitLatitude: "-32.883333", visitLongitude: "-68.816667"},
  {name: "Iquitos", country_id: Country.all[30].id, theme_id: Theme.find_by(:name => "Immersion in native population").id, description: "It is known as the \"capital of the Peruvian Amazon.\" The city is located in the Great Plains of the Amazon Basin, surrounded by the Amazon, Nanay and Itaya rivers. Overall, it constitutes Metropolitan Iquitos, a conurbation of 471,993 inhabitants consisting of four districts: Iquitos, Punchana, Belén, and San Juan Bautista.", image: "http://www.journeymachupicchu.com/wp-content/uploads/2015/07/IQUITO-1.jpg", visitLatitude: "-3.75", visitLongitude: "-73.25"},
  {name: "Calchaquí Valleys", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "The Calchaquí Valley (Spanish: Valles Calchaquíes) is an area in the northwestern region of Argentina which crosses the provinces of Catamarca, Tucumán, Jujuy and Salta. It is best known for its contrast of colours and its unique geography that ranges from the mountain desert to the subtropical forest.", image: "https://a.travel-assets.com/mediavault.le/media/957b04bcc531b10f8d0b85f393e631d6798f113a.jpeg", visitLatitude: "-26.94", visitLongitude: "-65.34"},
  {name: "Buenos Aires", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Immersion in native population").id, description: "Buenos Aires defines itself as a multicultural city, being home to multiple ethnic and religious groups. Also, several languages are spoken in the city in addition to Spanish, contributing to its culture and the dialect spoken in the city and in some other parts of the country.", image: "https://johnbarwoodphotography.files.wordpress.com/2012/04/buenos-aires-4.jpg", visitLatitude: "-34.603333", visitLongitude: "-58.381667"},
  {name: "Punta Tombo", country_id: Country.all[32].id, theme_id: Theme.find_by(:name => "Adventurous/Off the beaten tracks").id, description: "Punta Tombo is a peninsula into the Atlantic Ocean 110 km (68 mi) south of Trelew in Chubut Province, Argentina, where there is a large colony of Magellanic penguins - the largest such colony in South America.", image: "http://a7004596b03128c36bd2-313eee040a6739ed30827c353f729c6b.r55.cf1.rackcdn.com/98/1/large.jpg", visitLatitude: "-44.04", visitLongitude: "-65.2"}
])

